import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import Joi from 'joi-browser';
import Input from './common/input';
import Button from './common/button';


class RegisterForm extends Component {
    state = {
        data: { username: "", email: "", password: "" },
        errors: {}
    }

    schema = {
        username: Joi.string().min(3).max(10).required(),
        email: Joi.string().email().required(),
        password: Joi.string().min(3).required()
    }

    handleChange = ({ currentTarget: input }) => {
        const data = { ...this.state.data };
        data[input.name] = input.value;
        this.setState({ data });
    }
    validate = () => {
        const { data } = this.state;
        const options = { abortEarly: false }
        const res = Joi.validate(data, this.schema, options);

        if (!res.error) return null;

        const errors = {};
        for (let item of res.error.message)
            errors[item.path[0]] = item.message;
        return errors;
    }
    handleSubmit = e => {
        e.preventDefault();
        const errors = this.validate();
        this.setState({ errors: errors || {} })
        console.log("Submitted");
    }

    renderInput = (name, placeholder, type = "text") => {
        return <Input
            type={type}
            name={name}
            errors={this.state.errors}
            placeholder={placeholder}
            onChange={this.handleChange}
        />
    }

    renderButton = (label, type = "submit") => {
        return <Button
            type={type}
            label={label}
        />
    }
    render() {
        return (
            <form className="form-container" onSubmit={this.handleSubmit}>
                <h1 className="form-header">Sign up to join with Worldâ€™s greatest community</h1>
                {this.renderInput("username", "yourname")}
                {this.renderInput("email", "youremail@gmail.com")}
                {this.renderInput("password", "password", "password")}
                {this.renderButton("SignUp")}
                <p className="sub">Already have an account? <NavLink to='/login'>Login</NavLink></p>
            </form>
        );
    }
}

export default RegisterForm;
