import React, { Component } from 'react';
import Joi from 'joi-browser';
import Button from '../common/button';
import Input from '../common/input';
class SignUp extends Component {
    state = {
        data: { username: "", name: "", password: "" },
        errors: true
    }

    schema = {
        username: Joi.string().required().email().label("Username"),
        name: Joi.string().required().min(4).label("Name"),
        password: Joi.string().required().min(8).label("Password")
    }

    handleChange = ({ currentTarget: input }) => {
        const data = { ...this.state.data };
        data[input.name] = input.value;
        this.setState({ data });
    }

    validate = () => {
        const options = { abortEarly: false };
        const { error } = Joi.validate(this.state.data, this.schema, options);
        if (!error) return null;


        console.log(validate);
    }

    doSubmit = e => {
        e.preventDefault();
        this.validate();
        console.log("Submitted");
    }

    render() {
        const { errors } = this.state;
        return (
            <div id="form">

                <form id="form-container" onSubmit={this.doSubmit}>
                    <h1>SignUp</h1>
                    <Input name="username" placeholder="Username" type="text" handleChange={this.handleChange} />
                    <Input name="name" placeholder="Name" type="text" handleChange={this.handleChange} />
                    <Input name="password" placeholder="Password" type="password" handleChange={this.handleChange} />
                    <Button label="SignUp" />
                </form>
            </div>
        );
    }
}

export default SignUp;
