import React, { useEffect, useState } from 'react';
import { Route, Routes, Navigate, Switch, Router } from 'react-router-dom';
import NavBar from './components/navBar';
import Footer from './components/footer';
import LandingPage from './components/landingPage';
import RegisterForm from './components/registerForm';
import LoginForm from './components/loginForm';
import PublishForm from './components/publishForm';
import AllBlogs from './components/allBlogs';
import Logout from './components/logout';
import auth from './service/authService';
import ProtectRoute from './components/protectedRoutes';

function App() {
  const [user, setUser] = useState(false);
  useEffect(() => {
    const user = auth.getCurrentUser();
    setUser(user);
  }, [])
  return (
    <React.Fragment>
      <NavBar user={user} />
      <Routes>
        <Route path='/' Component={LandingPage} />
        <Route path='/register' Component={LandingPage} />
        <Route path='/' element={<ProtectRoute path="/register" component={RegisterForm} navigatePath="/blog" />} />
        <Route path='/' element={<ProtectRoute path="/login" component={LoginForm} navigatePath="/blog" />} />
        <Route path='/login' Component={LoginForm} />
        <Route path='/publish' element={user && user.isAdmin ? <PublishForm /> : <Navigate to="/login" />} />
        <Route path='/blog' Component={AllBlogs} />
        <Route path='/logout' Component={Logout} />
      </Routes>
      <Footer />
    </React.Fragment >
  );
}

export default App;
