import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import http from '../service/httpService';


const BlogPage = () => {
    const [postInfo, setPostInfo] = useState(false);
    const { id } = useParams();
    const getData = async () => {
        const { data } = await http.get(`http://localhost:4000/post/${id}`);
        setPostInfo(data);
        windows.location = `/blog/${id}`;
    }
    useEffect(() => {
        getData();
    });
    return (
        <div className="blog">
            <h1 className='blog-header'>{postInfo.title}</h1>
            <img src={"http://localhost:4000/" + postInfo.cover} className="blog-img" alt="" />
            <div dangerouslySetInnerHTML={{ __html: postInfo.content }} id="content" />
        </div>
    );
}

export default BlogPage;
