import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

const PublishForm = () => {
    const [post, setPost] = useState({
        title: "",
        description: "",
        file: "",
        content: ""
    });
    const modules = {
        toolbar: [
            [{ header: [1, 2, false] }],
            ['bold', 'italic', 'underline', 'strike', 'blockquote'],
            [
                { list: 'ordered' },
                { list: 'bullet' },
                { indent: '-1' },
                { indent: '+1' },
            ],
            ['link', 'image'],
            ['clean'],
        ],
    };

    const handleChange = ({ currentTarget: input }) => {
        const data = { ...post };
        data[input.name] = input.value;
        console.log(data);
        setPost(data);
    }


    return (
        <form className="form-container" id="pb-form">
            <h1 className="form-header">Post your creative blog here!</h1>
            <input type="text" className="input" placeholder='Title' name='title' onChange={handleChange} /><br />
            <input type="text" className="input" placeholder='Description' name='description' /><br />
            <input type="file" id="f-input" name='file' />
            <ReactQuill className='Quill' modules={modules} name='content' />
            <button className="btn" id="form-btn">Publish</button>
        </form>
    );
}

export default PublishForm;
