import React, { useState } from 'react';
import http from '../services/httpService';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import '../style/blogform.css';

function BlogForm() {

    const [title_, setTitle] = useState("");
    const [summary_, setSummary] = useState("");
    const [content_, setContent] = useState("");
    const [files_, setFiles] = useState("");

    async function doSumbit(e) {
        const values = new FormData();
        data.set('title', title);
        data.set('summary', summary);
        data.set('cover', files[0]);
        data.set('content', content);

        e.preventDefault();
        const res = await http.post("http://localhost:4000/post",
            {
                title: title_,
                summary: summary_,
                content: content_,
                cover: files_[0]
            });
        console.log(res);
    }
        return (
            <React.Fragment>
                <div id="form">
                    <form id="container" onSubmit={doSumbit}>
                        <h1>Create Blog</h1>
                        <input
                            type="title"
                            placeholder={'Title'}
                            id="getter"
                            onChange={e => { setTitle(e.target.value) }} />
                        <input type="summary"
                            placeholder={'Summary'}
                            id="getter"
                            onChange={e => { setSummary(e.target.value) }}
                        />
                        <input type="file"
                            id="getter"
                            onChange={e => { setFiles(e.target.files) }} />
                        <ReactQuill id="quill"
                            onChange={newValue => setContent(newValue)} />
                        <button
                            type="submit"
                            id="publish-button">
                            Publish
                        </button>
                    </form>
                </div>
            </React.Fragment>
        );
}

export default BlogForm;
