import React, { Component } from 'react';

class Testing extends Component {
    state = { text: "" }
    textContent = e => {
        const text = e.currentTarget.value;
        this.setState({ text });
        console.log(text);
    }
    render() {
        return (
            <div>
                <form>
                    <input type="text" onChange={this.textContent} />
                    <button type='submit'>Submit</button>
                </form>
            </div>
        );
    }
}

export default Testing;
