import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';


class RegisterForm extends Component {
    state = {
        data: { username: "", email: "", password: "" }
    }

    handleChange = ({ currentTarget: input }) => {
        const data = { ...this.state.data };
        data[input.name] = input.value;
        this.setState({ data });
    }
    handleSubmit = e => {
        e.preventDefault();
        console.log("Submitted")
    }
    render() {
        return (
        <div className="form-container">
            <h1 className="form-header">Sign up to join with Worldâ€™s greatest community</h1>
                <input type="text" className="input" name="username" placeholder='yourname' onChange={this.handleChange} /><br />
                <input type="text" className="input" name="email" placeholder='yourname@gmail.com' onChange={this.handleChange} /><br />
                <input type="password" className="input" name="password" placeholder='password' onChange={this.handleChange} /><br />
            <button className="btn" id="form-btn">SignUp</button>
            <p className="sub">Already have an account? <NavLink to='/login'>Login</NavLink></p>
        </div>
        );
    }
}

export default RegisterForm;
