import React, { useState } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import http from '../service/httpService';
import apiUrl from '../config.json';

const PublishForm = () => {
    const [post, setPost] = useState({
        title: "",
        description: "",
        file: "",
    });
    const [content, setContent] = useState("");
    const modules = {
        toolbar: [
            [{ header: [1, 2, false] }],
            ['bold', 'italic', 'underline', 'strike', 'blockquote'],
            [
                { list: 'ordered' },
                { list: 'bullet' },
                { indent: '-1' },
                { indent: '+1' },
            ],
            ['link', 'image'],
            ['clean'],
        ],
    };

    const doSubmit = async (e) => {
        e.preventDefault();
        const { apiUrl: ApiUrl } = apiUrl;
        const apiEndpoint = ApiUrl + "api/post";
        const res = await http.post(apiEndpoint, {
            title: post.title,
            description: post.description,
            cover: post.file,
            content: content
        });
        console.log(res);
    }

    const handleChange = ({ currentTarget: input }) => {
        const data = { ...post };
        data[input.name] = input.value;
        setPost(data);
    }

    const renderInput = (placeholder, name, type = "text") => {
        return (<>
            <input
                type={type}
                className="input"
                placeholder={placeholder}
                name={name}
                onChange={handleChange}
            /><br />
        </>);
    }


    return (
        <form className="form-container" id="pb-form" onSubmit={doSubmit}>
            <h1 className="form-header">Post your creative blog here!</h1>
            {renderInput("Title", "title")}
            {renderInput("Description", "description")}
            {renderInput("File", "file", "file")}
            <ReactQuill className='Quill' modules={modules} name='content' onChange={newValue => (setContent(newValue))} />
            <button type="submit" className="btn" id="form-btn">Publish</button>
        </form>
    );
}

export default PublishForm;
