import React from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

const PublishForm = () => {
    return (
        <div className="form-container">
            <h1 className="form-header">Login to Experience more about the world.</h1>
            <input type="text" className="input" placeholder='Title' /><br />
            <input type="text" className="input" placeholder='Description' /><br />
            <input type="image" className="input" placeholder='Description' /><br />
            <ReactQuill />
            <button className="btn" id="form-btn">Publish</button>
        </div>
    );
}

export default PublishForm;
