import React from 'react';
import '../style/signUp.css';
const Input = ({ type, name, placeholder, error }) => {
    return (
        <React.Fragment>
            <input id="text-field"
                type={type}
                placeholder={placeholder}
                name={name}
            />
            {error && <div>Enter valid input</div>}
        </React.Fragment>
    );
}

export default Input;
