        // }
    }
    render() {
        return (
            <div id="form">
                <form id="form-container" onSubmit={this.handleSubmit}>
                    <h1>Login</h1>
                    {this.renderInput("username", "Username")}
                    {this.renderInput("password", "Password", "password")}
                    {this.renderButton("Login")}
                </form>
            </div>
        );
    }
}

export default Login;
